from collections import deque

def solve():
    n = int(input())
    u = list(map(int, input().split()))
    d = list(map(int, input().split()))
    l = list(map(int, input().split()))
    r = list(map(int, input().split()))

    q = deque()
    for i in range(n):
        q.append('U' + str(i + 1))
        q.append('D' + str(i + 1))
        q.append('L' + str(i + 1))
        q.append('R' + str(i + 1))

    ans = []
    for i in range(n * n):
        x = q.popleft()
        if x[0] == 'U':
            u[i % n] -= 1
        elif x[0] == 'D':
            d[i % n] -= 1
        elif x[0] == 'L':
            l[i // n] -= 1
        elif x[0] == 'R':
            r[i // n] -= 1

        if u[i % n] == 0 and d[i % n] == 0 and l[i // n] == 0 and r[i // n] == 0:
            continue

        if u[i % n] < 0 or d[i % n] < 0 or l[i // n] < 0 or r[i // n] < 0:
            print('NO')
            return

        if x[0] == 'U' and u[i % n] > 0:
            q.appendleft('U' + str(i % n + 1))
        elif x[0] == 'D' and d[i % n] > 0:
            q.appendleft('D' + str(i % n + 1))
        elif x[0] == 'L' and l[i // n] > 0:
            q.appendleft('L' + str(i // n + 1))
        elif x[0] == 'R' and r[i // n] > 0:
            q.appendleft('R' + str(i // n + 1))
        else:
