        int u, d, l, r;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        Socket s = new Socket();
        st = new StringTokenizer(br.readLine());
        s.u = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        s.d = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        s.l = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        s.r = Integer.parseInt(st.nextToken());
        int[][] grid = new int[n][n];
        ArrayList<String> ans = new ArrayList<>();
        int cnt = 0;
        while (true) {
            boolean flag = false;
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    if (grid[i][j] < s.u) {
                        for (int k = 0; k < s.u - grid[i][j]; k++) {
                            if (i == 0) {
                                ans.add("U" + (j + 1));
                                grid[i][j]++;
                                cnt++;
                                flag = true;
                            } else if (grid[i - 1][j] == n) {
                                ans.add("U" + (j + 1));
                                grid[i][j]++;
                                cnt++;
                                flag = true;
                            }
                        }
                    }
                    if (grid[i][j] < s.d) {
                        for (int k = 0; k < s.d - grid[i][j]; k++) {
                            if (i == n - 1) {
                                ans.add("D" + (j + 1));
                                grid[i][j]++;
                                cnt++;
                                flag = true;
                            } else if (grid[i + 1][j] == n) {
                                ans.add("D" + (j + 1));
                                grid[i][j]++;
                                cnt++;
                                flag = true;
                            }
                        }
                    }
                    if (grid[i][j] < s.l) {
                        for (int k = 0; k < s.l - grid[i][j]; k++) {
                            if (j == 0) {
                                ans.add("L" + (i + 1));
                                grid[i][j]++;
                                cnt++;
                                flag = true;
                            } else if (grid[i][j - 1] == n) {
                                ans.add("L" + (i + 1));
                                grid[i][j]++;
                                cnt++;
                                flag = true;
                            }
                        }
                    }
                    if (grid[i][j] < s.r) {
                        for (int k = 0; k < s.r - grid[i][j]; k++) {
                            if (j == n - 1) {
                                ans.add("R" + (i + 1));
                                grid[i][j]++;
                                cnt++;
                                flag = true;
                            } else if (grid[i][j + 1] == n) {
                                ans.add("R" + (i + 1));
                                grid[i][j]++;
                                cnt++;
                                flag = true;
                            }
                        }
                    }
                }
            }
            if (!flag) break;
        }
        if (cnt != n * n) {
            System.out.println("NO");
        } else {
            Collections.sort(ans);
            for (String str : ans) {
                System.out.println(str);
            }
        }
    }
}
