        cout << 0 << endl;
        return 0;
    }

    vector<int> dp(256, 0);
    dp[0] = 1;
    for (int i = 0; i < n; i++) {
        vector<int> ndp(256, 0);
        for (int j = 0; j < 256; j++) {
            if (dp[j] == 0) continue;
            ndp[j] = (ndp[j] + dp[j]) % MOD;
            if (j + a[i] < 256) {
                ndp[j + a[i]] = (ndp[j + a[i]] + dp[j]) % MOD;
            }
        }
        swap(dp, ndp);
    }

    int ans = 0;
    for (int i = 0; i < 256; i++) {
        if (cnt[i] % 2 == 1) {
            ans = (ans + dp[i]) % MOD;
        } else {
            ans = (ans + MOD - dp[i]) % MOD;
        }
    }

    cout << ans << endl;

