MOD = 10**9 + 7

def pow_mod(a, b):
    res = 1
    while b:
        if b & 1:
            res = res * a % MOD
        a = a * a % MOD
        b >>= 1
    return res

def solve():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))
    cnt = [0] * (n + 1)
    for i in a:
        cnt[i] = 1
    dp = [0] * (k + 1)
    dp[0] = 1
    for i in range(1, n + 1):
        for j in range(k, -1, -1):
            if j >= a[i - 1]:
                dp[j] = (dp[j] + dp[j - a[i - 1]]) % MOD
    ans = 0
    for i in range(1, k + 1):
        if i * i > k:
            break
        if k % i == 0:
            if i % 2 == 0:
                ans = (ans + dp[i] * pow_mod(2, (k // i - 1) * (n - cnt[i]) - 1, MOD)) % MOD
            else:
                ans = (ans + dp[i] * pow_mod(2, (k // i - 1) * (n - cnt[i]) - 1, MOD) * (n - cnt[i])) % MOD
            if k // i != i:
                ans = (ans + dp[k // i] * pow_mod(2, (i - 1) * (n - cnt[i]) - 1, MOD)) % MOD
    print(ans)

if __name__ == "__main__":
    solve()
