        int q = sc.nextInt();
        long[] a = new long[n + 1];
        long[] b = new long[q + 1];
        for (int i = 1; i <= q; i++) {
            a[i] = sc.nextLong();
            b[i] = sc.nextLong();
        }
        long[] ans = new long[n + 1];
        TreeSet<Long> set = new TreeSet<>();
        for (int i = 1; i <= n; i++) {
            set.add((long) i);
