        for (int i = 1; i <= q; i++) {
            long x = a[i];
            long y = b[i];
            long val = set.first();
            long cnt = 0;
            while (true) {
                long l = set.lower(val);
                long r = set.higher(val);
                if (l == null) {
                    set.remove(val);
                    break;
                } else if (r == null) {
                    set.remove(val);
                    cnt += (val - l) * y;
                    set.remove(l);
                    val = l;
                } else {
                    long min = Math.min((val - l) * y, (r - val) * y);
                    cnt += min;
                    if (min == (val - l) * y) {
                        set.remove(val);
                        val = l;
                    } else {
                        set.remove(val);
                        val = r;
                    }
                }
            }
            ans[(int) val] += cnt;
        }
        for (int i = 1; i <= n; i++) {
            out.println(ans[i]);
        }
        out.close();
    }
