using namespace std;

const int64_t MOD = 998244353;

int64_t modpow(int64_t a, int64_t n) {
    int64_t res = 1;
    while (n) {
        if (n & 1) res = res * a % MOD;
        a = a * a % MOD;
        n >>= 1;
    }
    return res;
}

int64_t inv(int64_t a) {
    return modpow(a, MOD - 2);
}

int64_t nCr(int64_t n, int64_t r) {
    if (r > n) return 0;
    int64_t res = 1;
    for (int64_t i = 1; i <= r; i++) {
        res = res * (n - i + 1) % MOD * inv(i) % MOD;
    }
    return res;
}

int64_t solve(int64_t n, int64_t m) {
    vector<int64_t> a(n + 1);
    a[1] = 1;
    a[2] = 1;
    for (int64_t i = 3; i <= n; i++) {
        a[i] = (a[i - 1] + a[i - 2]) % MOD;
    }

    vector<int64_t> d(n + 1, 0);
    d[1] = a[1];
    for (int64_t i = 2; i <= n; i++) {
        for (int64_t j = 1; j <= m; j++) {
            d[i][j] = (d[i - 1][j] + d[i - 1][j - 1]) % MOD;
        }
    }

    int64_t res = 0;
    for (int64_t i = 1; i <= n; i++) {
        int64_t coef = nCr(n, i);
        int64_t term = 0;
        for (int64_t j = 0; j <= m; j++) {
            term = (term + coef * a[i] % MOD * modpow(i, m - j) % MOD * d[i][j]) % MOD;
        }
        res = (res + term) % MOD;
    }

    return res;
}

int main() {
    int64_t n, m;
    cin >> n >> m;
    cout << solve(n, m) << endl;
    return 0;
}
