MOD = 998244353

def add(a, b):
    return (a + b) % MOD

def mul(a, b):
    return (a * b) % MOD

def binpow(a, b):
    res = 1
    while b:
        if b & 1:
            res = mul(res, a)
        a = mul(a, a)
        b >>= 1
    return res

def solve(n, m):
    if m == 0:
        return 0
    if n == 1:
        return 1
    if n == 2:
        return add(mul(mul(2, m + 1), 1), mul(mul(mul(2, m), m + 1), 1))
    fib = [0] * (n + 1)
    fib[1] = 1
    fib[2] = 1
    for i in range(3, n + 1):
        fib[i] = add(fib[i - 1], fib[i - 2])
    res = 0
    for i in range(1, n + 1):
        res = add(res, mul(fib[i], binpow(fib[i], m)))
    return res

n, m = map(int, input().split())
print(solve(n, m))
