import sys
import random

class Query:
    def __init__(self, x1, y1, x2, y2):
        self.x1 = x1
        self.y1 = y1
        self.x2 = x2
        self.y2 = y2

    def __hash__(self):
        return hash((self.x1, self.y1, self.x2, self.y2))

    def __eq__(self, other):
        return (self.x1, self.y1, self.x2, self.y2) == (other.x1, other.y1, other.x2, other.y2)

class Answer:
    def __init__(self, x, y, value):
        self.x = x
        self.y = y
        self.value = value

    def __hash__(self):
        return hash((self.x, self.y, self.value))

    def __eq__(self, other):
        return (self.x, self.y, self.value) == (other.x, other.y, other.value)

class TrieNode:
    def __init__(self):
        self.children = {}
        self.answer = None

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, query, answer):
        node = self.root
        for i in range(4):
            if query.x1 >> i & 1:
                if 1 not in node.children:
                    node.children[1] = TrieNode()
                node = node.children[1]
            else:
                if 0 not in node.children:
                    node.children[0] = TrieNode()
                node = node.children[0]
        node.answer = answer

    def query(self, query):
        node = self.root
        for i in range(4):
            if query.x1 >> i & 1:
                if 0 in node.children:
                    node = node.children[0]
                else:
                    node = node.children[1]
            else:
                if 1 in node.children:
                    node = node.children[1]
                else:
                    node = node.children[0]
        return node.answer

def main():
    H, W, N, K = map(int, input().split())
    queries = []
    answers = []
    trie = Trie()
    for i in range(H):
        for j in range(W):
            queries.append(Query(i, j, i, j))
            answers.append(Answer(i, j, 0))
    random.shuffle(queries)
    for query in queries:
        sys.stdout.write(f'? {query.x1} {query.y1} {query.x2} {query.y2}\n')
        sys.stdout.flush()
        value = int(input())
        answers[queries.index(query)].value = value
        trie.insert(query, answers[queries.index(query)])
    sys.stdout.write('!')
    for i in range(H):
        for j in range(W):
            answer = trie.query(Query(i, j, i, j))
            sys.stdout.write(f' {answer.value << (i * W + j)}')
    sys.stdout.write(f' {sum(answer.value << (i * W + j) for answer in answers) % K}\n')

if __name__ == '__main__':
    main()
