#include <stdio.h>
#include <stdbool.h>

int main() {
    int h, w;
    scanf("%d %d", &h, &w);

    char grid[h][w + 1];
    for (int i = 0; i < h; i++) {
        scanf("%s", grid[i]);
    }

    bool visited[h][w];
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            visited[i][j] = false;
        }
    }

    bool possible = true;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (grid[i][j] == '#') {
                if (i > 0 && grid[i - 1][j] == '#') {
                    visited[i][j] = true;
                }
                if (j > 0 && grid[i][j - 1] == '#') {
                    visited[i][j] = true;
                }
            }
        }
    }

    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (grid[i][j] == '#' && !visited[i][j]) {
                possible = false;
            }
        }
    }

    if (possible) {
        printf("Possible\n");
    } else {
        printf("Impossible\n");
    }

    return 0;
}
