```python
def is_possible(grid):
    h, w = len(grid), len(grid[0])
    visited = [[False]*w for _ in range(h)]

    def dfs(i, j):
        if i == h-1 and j == w-1:
            return True
        if visited[i][j]:
            return False
        visited[i][j] = True
        if grid[i][j] == '#':
            return dfs(i+1, j) or dfs(i, j+1)
        return dfs(i+1, j) or dfs(i, j+1)

    return dfs(0, 0)

h, w = map(int, input().split())
grid = [list(input()) for _ in range(h)]
print("Possible" if is_possible(grid) else "Impossible")
