#include <stdio.h>
#include <math.h>

#define N 200000
#define D 100

long long int f[N + 1][N + 1];

int main() {
    int n, d1, x;
    scanf("%d %d %d", &n, &d1, &x);
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            f[i][j] = 0;
        }
    }
    f[0][0] = 1;
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            if (f[i][j] == 0) continue;
            if (i == n) {
                f[i][j] = (d1 + (j - 1) * x) * f[i][j];
                continue;
            }
            f[i + 1][j] += (d1 + (j - 1) * x) * f[i][j] / (2 * (i + 1));
            f[i + 1][j + 1] += (d1 + (j - 1) * x) * f[i][j] / (2 * (i + 1));
        }
    }
    double ans = 0;
    for (int i = 0; i <= n; i++) {
        ans += (double)f[n][i] / f[n][0] * (d1 + (i - 1) * x);
    }
    printf("%.10lf\n", ans);
    return 0;
}
