#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 131072;

int n;
vector<int> a[N];
int v[N];
int sz[N];
int ans = 1e9;

void dfs(int u, int p) {
    sz[u] = 1;
    for (int v : a[u]) {
        if (v != p) {
            dfs(v, u);
            sz[u] += sz[v];
        }
    }
}

void dfs2(int u, int p, int d, int s, int t) {
    if (d == 0) {
        int sum = 0;
        for (int v : a[u]) {
            if (v != p) {
                sum += v[v.size() - 1];
            }
        }
        ans = min(ans, sum);
    } else {
        for (int v : a[u]) {
            if (v != p) {
                dfs2(v, u, d - 1, s, t);
            }
        }
        if (s < sz[u] - s) {
            for (int v : a[u]) {
                if (v != p) {
                    dfs2(v, u, d - 1, s + 1, t);
                }
            }
        }
    }
}

int main() {
    cin >> n;
    for (int i = 1; i < n; i++) {
        int a, v;
        cin >> a >> v;
        a--;
        v[N - 1] = v;
        for (int j = N - 2; j >= 0; j--) {
            v[j] = max(v[j], v[j + 1]);
        }
        a[i].push_back(v);
    }
    dfs(0, -1);
    dfs2(0, -1, n - 1, 1, n - 1);
    cout << ans << endl;
    return 0;
}
