public class Main {
    static int N;
    static int[] a, v;
    static List<Integer>[] g;
    static int[] sz;
    static int[] maxv;
    static int ans;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        a = new int[N];
        v = new int[N];
        g = new List[N];
        sz = new int[N];
        maxv = new int[N];
        for (int i = 0; i < N; i++) {
            g[i] = new ArrayList<>();
        }
        for (int i = 1; i < N; i++) {
            st = new StringTokenizer(br.readLine());
            a[i] = Integer.parseInt(st.nextToken()) - 1;
            v[i] = Integer.parseInt(st.nextToken());
            g[a[i]].add(i);
        }
        dfs(0, -1);
        int m = 0;
        for (int i = 0; i < N; i++) {
            if (g[i].size() == 0) m++;
        }
        int[] dp = new int[m + 1];
        for (int i = 1; i <= m; i++) {
            dp[i] = Integer.MAX_VALUE;
        }
        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= m; j++) {
                if (i + j - 1 <= m) {
                    int cur = 0;
                    for (int k = 0; k < i; k++) {
                        cur += maxv[find(k)];
                    }
                    for (int k = i; k < i + j - 1; k++) {
                        cur += v[find(k)];
                    }
                    for (int k = i + j - 1; k < i + j; k++) {
                        cur += maxv[find(k)];
                    }
                    dp[i + j - 1] = Math.min(dp[i + j - 1], cur);
                }
            }
        }
        pw.println(dp[m]);
        pw.close();
    }

    static int find(int x) {
        if (sz[x] == 0) return x;
        return x = find(x);
    }

    static void dfs(int x, int fa) {
        sz[x] = 1;
        for (int i : g[x]) {
            if (i != fa) {
                dfs(i, x);
                sz[x] += sz[i];
                maxv[x] = Math.max(maxv[x], v[x]);
                if (g[i].size() == 0) maxv[x] = Math.max(maxv[x], v[x]);
            }
        }
    }
}
