    static final int N = 1000000;
    static final int M = 26;
    static int[][] next = new int[N][M];
    static int[] fail = new int[N];
    static int[] len = new int[N];
    static int idx = 1;
    static int[] last = new int[N];
    static int[] dp = new int[N];
    static int[] dp2 = new int[N];
    static int[] s = new int[N];
    static int[] t = new int[N];
    static int n, m;

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        String s0 = sc.next();
        String t = sc.next();
        for (int i = 0; i < n; i++) {
            s[i + 1] = s0.charAt(i) - 'a';
            t[i + 1] = t.charAt(i) - 'a';
        }
        m = n;
        init();
        for (int i = 1; i <= n; i++) {
            insert(s[i]);
        }
        for (int i = 1; i <= n; i++) {
            dp[i] = dp2[i] = 0;
        }
        dp[0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j < M; j++) {
                if (next[fail[i]][j] != 0) {
                    dp[i] = Math.max(dp[i], dp[next[fail[i]][j]] + 1);
                }
            }
            dp2[i] = Math.max(dp2[i - 1], dp[i]);
        }
        int ans = n + 1;
        for (int i = 1; i <= n; i++) {
            if (dp2[i] >= m) {
                ans = Math.min(ans, i);
            }
        }
        System.out.println(ans == n + 1 ? -1 : ans);
    }

    static void init() {
        len[0] = -1;
        for (int i = 0; i < M; i++) {
            next[0][i] = 1;
        }
        for (int i = 1; i <= n; i++) {
            len[i] = len[fail[i]] + 1;
            for (int j = 0; j < M; j++) {
                next[i][j] = next[fail[i]][j];
            }
            while (len[next[i][s[i]]] == len[i]) {
                next[i][s[i]] = fail[next[i][s[i]]];
            }
            fail[i] = next[fail[i]][s[i]];
            last[i] = next[i][s[i]];
        }
    }

    static void insert(int c) {
        int p = 0;
        for (int i = 1; i <= n; i++) {
            if (next[p][c] == 0) {
                next[p][c] = ++idx;
            }
            p = next[p][c];
        }
    }
}
