#include <algorithm>
#include <cmath>

using namespace std;

typedef long long ll;

const ll INF = 1e18;

ll gcd(ll a, ll b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

ll lcm(ll a, ll b) {
    return a / gcd(a, b) * b;
}

void solve() {
    int n, t;
    cin >> n >> t;
    vector<ll> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }
    sort(a.begin(), a.end());
    ll ans = INF;
    for (int i = 0; i < n - 1; i++) {
        ll g = gcd(a[i], a[i + 1]);
        ll x = lcm(a[i], a[i + 1]);
        ll y = x / g;
        ll z = (y - a[i]) * (a[i + 1] / g) + (a[i + 1] / g - 1) * (y - a[i + 1]);
        if (z <= t) {
            ans = min(ans, abs(a[i + 1] - a[i]) + abs(y - a[i]) + abs(y - a[i + 1]));
        }
    }
    cout << ans << endl;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    int t = 1;
    while (t--) {
        solve();
    }
    return 0;
}
