#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

typedef long long ll;

const ll INF = 1e18;

ll gcd(ll a, ll b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

ll lcm(ll a, ll b) {
    return a / gcd(a, b) * b;
}

ll solve(vector<ll>& a, ll t) {
    ll n = a.size();
    ll ans = 0;
    for (ll i = 0; i < n - 1; i++) {
        ll g = gcd(a[i], a[i + 1]);
        ll x = lcm(a[i], a[i + 1]);
        ll y = x / g;
        ll z = (y - a[i]) * (a[i + 1] - a[i]);
        if (z <= t) {
            ans += a[i + 1] - a[i];
            t -= z;
        }
        else {
            ans += a[i + 1] - a[i];
            ans += (t + a[i]) * (a[i + 1] - a[i]) / (a[i + 1] - a[i] + a[i]);
            break;
        }
    }
    return ans;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    ll n, t;
    cin >> n >> t;
    vector<ll> a(n);
    for (ll i = 0; i < n; i++) {
        cin >> a[i];
    }
    ll ans = solve(a, t);
    if (ans == 0) {
        ans = 1;
    }
    cout << ans << '\n';

    return 0;
}
