#define N 100005

int n, k, cnt, root;
int a[N], b[N], p[N], f[N], g[N];
int vis[N], num[N], dep[N], sum[N];

void dfs(int u, int fa) {
    vis[u] = 1;
    num[u] = ++cnt;
    dep[u] = dep[fa] + 1;
    sum[u] = sum[fa] + (p[u] != 0);
    for (int i = 1; i <= k; i++) {
        if (a[i] == u) f[i] = fa;
        if (b[i] == u) f[i] = a[i];
    }
    for (int i = 1; i <= k; i++) {
        if (f[i] == 0 || vis[f[i]]) continue;
        dfs(f[i], u);
    }
}

int main() {
    int i, j;
    scanf("%d", &n);
    for (i = 1; i < n; i++) {
        scanf("%d %d", &a[i], &b[i]);
    }
    scanf("%d", &k);
    for (i = 1; i <= k; i++) {
        scanf("%d %d", &a[i + n - 1], &p[i]);
    }
    for (i = 1; i <= n; i++) {
        if (vis[i]) continue;
        root = i;
        dfs(i, 0);
        break;
    }
    if (sum[root] % 2 == 0) {
        for (i = 1; i <= n; i++) {
            if (p[i] != 0) g[i] = p[i];
            else g[i] = (num[i] % 2 == 0) ? sum[root] / 2 : sum[root] / 2 + 1;
        }
    } else {
        for (i = 1; i <= n; i++) {
            if (p[i] != 0) g[i] = p[i];
            else g[i] = (num[i] % 2 == 0) ? sum[root] / 2 + 1 : sum[root] / 2;
        }
    }
    for (i = 1; i <= n; i++) {
        if (p[i] != 0) continue;
        for (j = i; j; j = f[j]) {
            if (dep[j] % 2 == 0) g[j]++;
            else g[j]--;
        }
    }
    for (i = 1; i <= n; i++) {
        if (p[i] != 0) continue;
        if (g[i] < 0) {
            printf("No\n");
            return 0;
        }
    }
    printf("Yes\n");
    for (i = 1; i <= n; i++) {
        if (p[i] != 0) printf("%d\n", p[i]);
        else printf("%d\n", g[i]);
    }
    return 0;
}
