            graph[i] = new ArrayList<>();
        }
        for (int i = 0; i < n - 1; i++) {
            int u = scanner.nextInt() - 1;
            int v = scanner.nextInt() - 1;
            graph[u].add(new Pair(v, i));
            graph[v].add(new Pair(u, i));
        }
        int k = scanner.nextInt();
        value = new int[n];
        for (int i = 0; i < k; i++) {
            int u = scanner.nextInt() - 1;
            value[u] = scanner.nextInt();
        }
