        if (ok) {
            out.println("Yes");
            for (int i = 0; i < n; i++) {
                out.println(value[i] + color[i]);
            }
        } else {
            out.println("No");
        }
        out.close();
    }

    static boolean dfs(int u, int p) {
        for (Pair pair : graph[u]) {
            int v = pair.first;
            int id = pair.second;
            if (v == p) continue;
            if (color[v] != 0) {
                if (color[v] != color[u] && value[u] - value[v] != 1 && value[u] - value[v] != -1) return false;
