    for (int i = 0; i < n; i++) {
        if (x[i] < minx) minx = x[i];
        if (x[i] > maxx) maxx = x[i];
        if (y[i] < miny) miny = y[i];
        if (y[i] > maxy) maxy = y[i];
    }
    int ans = 2 * (minx + maxx) + 2 * (miny + maxy);
    for (int i = 0; i < n; i++) {
        if (x[i] == minx || x[i] == maxx) {
            ans = 0;
            break;
        }
        if (y[i] == miny || y[i] == maxy) {
            ans = 0;
            break;
        }
    }
    printf("%d\n", ans);
    return 0;
