public class Main {
    static class Pair implements Comparable<Pair> {
        int x, y;
        public Pair(int x, int y) {
            this.x = x;
            this.y = y;
        }
        public int compareTo(Pair other) {
            return Integer.compare(this.x, other.x);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int W = Integer.parseInt(st.nextToken());
        int H = Integer.parseInt(st.nextToken());
        int N = Integer.parseInt(st.nextToken());
        Pair[] points = new Pair[N];
        for (int i = 0; i < N; i++) {
            st = new StringTokenizer(br.readLine());
            points[i] = new Pair(Integer.parseInt(st.nextToken()), Integer.parseInt(st.nextToken()));
        }
        Arrays.sort(points);
        int[] x = new int[N + 1];
        int[] y = new int[N + 1];
        x[0] = 0;
        y[0] = 0;
        for (int i = 0; i < N; i++) {
            x[i + 1] = points[i].x;
            y[i + 1] = points[i].y;
        }
        x[N] = W;
        y[N] = H;
        int[] minx = new int[N + 1];
        int[] miny = new int[N + 1];
        int[] maxx = new int[N + 1];
        int[] maxy = new int[N + 1];
        minx[0] = 0;
        miny[0] = 0;
        maxx[0] = 0;
        maxy[0] = 0;
        for (int i = 0; i < N; i++) {
            minx[i + 1] = Math.min(minx[i], x[i]);
            miny[i + 1] = Math.min(miny[i], y[i]);
            maxx[i + 1] = Math.max(maxx[i], x[i]);
            maxy[i + 1] = Math.max(maxy[i], y[i]);
        }
        int ans = 0;
        for (int i = 0; i < N; i++) {
            int left = minx[i];
            int right = maxx[i + 1];
            int top = maxy[i + 1];
            int bottom = miny[i + 1];
            if (left > right || top < bottom) continue;
            int width = Math.max(right - left, top - bottom);
            int height = Math.max(right - left, bottom - top);
            int area = width * height;
            int perimeter = 2 * (width + height);
            ans = Math.max(ans, perimeter - 2 * area);
        }
        System.out.println(ans);
    }
}
