#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 100000
#define M 100000

typedef long long ll;

ll n, m, k;
ll x[N + 5], a[M + 5], ans[N + 5];
ll sum[N + 5];

void solve() {
    scanf("%lld", &n);
    for (ll i = 1; i <= n; i++) {
        scanf("%lld", &x[i]);
        sum[i] = sum[i - 1] + x[i];
    }
    scanf("%lld%lld", &m, &k);
    for (ll i = 1; i <= m; i++) {
        scanf("%lld", &a[i]);
    }
    for (ll i = 1; i <= n; i++) {
        ll l = 1, r = m;
        while (l < r) {
            ll mid = (l + r) >> 1;
            if (a[mid] < i) {
                l = mid + 1;
            } else {
                r = mid;
            }
        }
        if (l == 1) {
            ans[i] = sum[n];
        } else {
            ll p = (k + 1) / 2;
            ans[i] = (sum[n] + sum[i - 1] + sum[n] - sum[i - 1] - x[i] + x[a[l - 1]]) * (1.0 * p / k);
        }
    }
    for (ll i = 1; i <= n; i++) {
        printf("%.10lf\n", ans[i]);
    }
}

int main() {
    int t = 1;
    while (t--) {
        solve();
    }
    return 0;
}
