```java
import java.io.*;
import java.util.*;

public class Main {
    static final int N = 100000;
    static final int M = 100000;
    static final int K = 1000000000000000000L;

    static class FastScanner {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer("");

        String next() {
            while (!st.hasMoreTokens())
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    static long[] x = new long[N];
    static long[] ans = new long[N];
    static long[] sum = new long[N];
    static long[] cnt = new long[N];

    static void add(int i, long v) {
        sum[i] += v;
        cnt[i]++;
    }

    static void sub(int i, long v) {
        sum[i] -= v;
        cnt[i]--;
    }

    static void solve(int n) {
        for (int i = 1; i <= n; i++) {
            ans[i] = x[i];
            if (i > 1) add(i - 1, x[i]);
            if (i < n) add(i + 1, x[i]);
        }
        for (int i = 1; i <= n; i++) {
            if (cnt[i] == 0) continue;
            long v = sum[i] / cnt[i];
            ans[i] += (K - cnt[i]) * v;
            add(i, -v);
        }
    }

    public static void main(String[] args) {
        FastScanner sc = new FastScanner();
        PrintWriter out = new PrintWriter(System.out);
        int n = sc.nextInt();
        for (int i = 1; i <= n; i++) x[i] = sc.nextLong();
        int m = sc.nextInt();
        long k = sc.nextLong();
        for (int i = 1; i <= m; i++) {
            int a = sc.nextInt();
            if (a == 1) solve(n);
            else {
                solve(a - 1);
                solve(a);
            }
        }
        for (int i = 1; i <= n; i++) {
            out.printf("%.10f\n", ans[i] / k);
        }
        out.close();
    }
}
