import java.io.*;
import java.util.*;

public class Main {
    static int N;
    static int[] a;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        a = new int[2 * N - 1];
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < 2 * N - 1; i++) {
            a[i] = Integer.parseInt(st.nextToken());
        }
        pw.println(solve());
        pw.close();
    }

    static int solve() {
        int[] pos = new int[2 * N - 1];
        for (int i = 0; i < 2 * N - 1; i++) {
            pos[a[i] - 1] = i;
        }
        int[] p = new int[2 * N - 1];
        for (int i = 0; i < 2 * N - 1; i++) {
            p[i] = pos[i];
        }
        for (int i = 2 * N - 2; i >= 0; i--) {
            for (int j = 0; j < 2 * N - 1 - i; j++) {
                p[j] = Math.min(p[j], p[j + 1]);
            }
        }
        int[] l = new int[2 * N - 1];
        int[] r = new int[2 * N - 1];
        for (int i = 0; i < 2 * N - 1; i++) {
            l[i] = pos[i];
            r[i] = pos[i];
        }
        for (int i = 0; i < 2 * N - 1; i++) {
            if (l[i] > 0) {
                l[i] = l[i] - 1;
            }
            if (r[i] < 2 * N - 2) {
                r[i] = r[i] + 1;
            }
        }
        int[] min = new int[2 * N - 1];
        for (int i = 0; i < 2 * N - 1; i++) {
            min[i] = Math.min(Math.min(l[i], p[i]), r[i]);
        }
        int[] max = new int[2 * N - 1];
        for (int i = 0; i < 2 * N - 1; i++) {
            max[i] = Math.max(Math.max(l[i], p[i]), r[i]);
        }
        int[] med = new int[2 * N - 1];
        for (int i = 0; i < 2 * N - 1; i++) {
            med[i] = (max[i] + min[i]) / 2;
        }
        int[] val = new int[2 * N - 1];
        for (int i = 0; i < 2 * N - 1; i++) {
            val[i] = a[i];
        }
        for (int i = 2 * N - 3; i >= 0; i--) {
            for (int j = 0; j < 2 * N - 1 - i; j++) {
                val[j] = med[j];
            }
        }
        return val[0];
    }
}
