#define MAXN 100005

int n, m;
int a[MAXN], b[MAXN];
int in[MAXN], out[MAXN];
int vis[MAXN];
int cnt;

void dfs(int x) {
    if (vis[x]) return;
    vis[x] = 1;
    for (int i = 0; i < cnt; i++) {
        if (in[i] == x && !vis[out[i]]) {
            dfs(out[i]);
        }
    }
}

int main() {
    scanf("%d%d", &n, &m);
    for (int i = 0; i < m; i++) {
        scanf("%d%d", &a[i], &b[i]);
        in[a[i]]++;
        out[b[i]]++;
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (in[i] && out[j]) {
                in[i]--;
                out[j]--;
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        if (in[i]) {
            dfs(i);
        }
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        ans += in[i];
    }
    printf("%d\n", ans);
    return 0;
}
