    graph = defaultdict(set)
    in_degree = defaultdict(int)
    for _ in range(m):
        u, v = map(int, input().split())
        graph[u].add(v)
        in_degree[v] += 1
    queue = []
    for node in graph:
        if in_degree[node] == 0:
            queue.append(node)
    ans = m
    while queue:
        node = queue.pop()
        for neighbor in graph[node]:
            in_degree[neighbor] -= 1
            if in_degree[neighbor] == 0:
                queue.append(neighbor)
            ans += 1
    print(ans)

if __name__ == "__main__":
