    dp[1][1] = 1;

    for (int i = 2; i <= n; i++) {
        if (t[i] > t[i - 1]) {
            dp[i][0] = (dp[i - 1][0] + dp[i - 1][1]) % mod;
            dp[i][1] = dp[i - 1][1];
        } else if (a[i] < a[i - 1]) {
            dp[i][0] = dp[i - 1][0];
            dp[i][1] = (dp[i - 1][0] + dp[i - 1][1]) % mod;
        } else {
            dp[i][0] = 0;
            dp[i][1] = 0;
        }
    }

    int ans = (dp[n][0] + dp[n][1]) % mod;
    for (int i = 1; i <= n; i++) {
        if (t[i] != a[i]) {
            ans = 0;
            break;
        }
    }

    printf("%d\n", ans);
