#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    int n;
    cin >> n;

    vector<int> t(n), a(n);
    for (int i = 0; i < n; i++) {
        cin >> t[i];
    }
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    int l = 0, r = n - 1;
    while (l < n && t[l] == a[l]) l++;
    while (r >= 0 && t[r] == a[r]) r--;

    if (l > r) {
        cout << 1 << endl;
        return 0;
    }

    vector<int> t1, a1;
    for (int i = 0; i < l; i++) t1.push_back(t[i]);
    for (int i = r + 1; i < n; i++) a1.push_back(a[i]);

    sort(t1.begin(), t1.end());
    sort(a1.begin(), a1.end());

    vector<int> t2, a2;
    for (int i = 0; i < l; i++) t2.push_back(t[i]);
    for (int i = r + 1; i < n; i++) a2.push_back(a[i]);

    int ans = 1;
    for (int i = 0; i < t2.size(); i++) {
        int x = t2[i] - t2[i - 1];
        ans = (1LL * ans * x) % MOD;
    }

    for (int i = 0; i < a2.size(); i++) {
        int x = a2[i] - a2[i - 1];
        ans = (1LL * ans * x) % MOD;
    }

    cout << ans << endl;

    return 0;
}
