MOD = 10**9 + 7

def pow_mod(a, b):
    res = 1
    while b:
        if b & 1:
            res = (res * a) % MOD
        a = (a * a) % MOD
        b >>= 1
    return res

def solve():
    n = int(input())
    t = list(map(int, input().split()))
    a = list(map(int, input().split()))

    if t[-1] < a[-1]:
        print(0)
        return

    if t[0] > a[0]:
        t[0] = a[0]

    dp = [0] * (n + 1)
    dp[0] = 1

    for i in range(1, n + 1):
        if i == 1:
            if t[0] == a[0]:
                dp[i] = (dp[i - 1] * (a[0] - 1)) % MOD
            else:
                dp[i] = (dp[i - 1] * (t[0] - 1)) % MOD
        else:
            if t[i - 1] == a[i - 1]:
                dp[i] = (dp[i - 1] * (a[i - 1] - 1)) % MOD
            else:
                dp[i] = (dp[i - 1] * (t[i - 1] - 1)) % MOD

            if t[i - 1] > a[i - 1]:
                dp[i] = (dp[i] + (dp[i - 1] * (a[i - 1] - 1)) % MOD) % MOD

    ans = 0
    for i in range(1, n + 1):
        if t[i - 1] == a[i - 1]:
            ans = (ans + pow_mod(i, a[i - 1] - 1)) % MOD
        else:
            ans = (ans + pow_mod(i, t[i - 1] - 1)) % MOD

    print((dp[n] * ans) % MOD)

solve()
