```java
import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static int N;
    static int[] T, A;
    static long[] dp;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st;

        N = Integer.parseInt(br.readLine());
        T = new int[N];
        A = new int[N];
        dp = new long[N + 1];

        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < N; i++) {
            T[i] = Integer.parseInt(st.nextToken());
        }

        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < N; i++) {
            A[i] = Integer.parseInt(st.nextToken());
        }

        if (T[N - 1] < A[N - 1]) {
            pw.println(0);
            pw.close();
            return;
        }

        for (int i = 0; i <= N; i++) {
            dp[i] = -1;
        }

        pw.println(solve(0, 0));
        pw.close();
    }

    static long solve(int i, int j) {
        if (i == N) {
            return j == 0 ? 1 : 0;
        }

        if (dp[i] != -1) {
            return dp[i];
        }

        long ans = 0;
        int t = T[i], a = A[i];

        if (t == a) {
            ans = (ans + solve(i + 1, j)) % MOD;
        }

        if (t > a) {
            ans = (ans + solve(i + 1, j + 1) * (t - a)) % MOD;
        }

        if (t < a) {
            ans = (ans + solve(i + 1, j) * (a - t)) % MOD;
        }

        return dp[i] = ans;
    }
}
