#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007
#define MAXN 500000

long long factorial[MAXN + 1];
long long factorial_inv[MAXN + 1];

void init_factorial() {
    factorial[0] = 1;
    for (int i = 1; i <= MAXN; i++) {
        factorial[i] = (factorial[i - 1] * i) % MOD;
    }
    factorial_inv[MAXN] = pow_mod(factorial[MAXN], MOD - 2, MOD);
    for (int i = MAXN - 1; i >= 0; i--) {
        factorial_inv[i] = (factorial_inv[i + 1] * (i + 1)) % MOD;
    }
}

long long pow_mod(long long a, long long b, long long mod) {
    long long res = 1;
    while (b > 0) {
        if (b & 1) {
            res = (res * a) % mod;
        }
        a = (a * a) % mod;
        b >>= 1;
    }
    return res;
}

int main() {
    int n;
    scanf("%d", &n);
    int p[n + 1];
    for (int i = 1; i <= n; i++) {
        scanf("%d", &p[i]);
    }
    init_factorial();
    long long ans = 0;
    for (int i = 1; i <= n; i++) {
        if (p[i] == 0) {
            int cnt = 0;
            for (int j = i + 1; j <= n; j++) {
                if (p[j] == 0) {
                    cnt++;
                } else {
                    break;
                }
            }
            long long ways = factorial[n - i] * factorial_inv[cnt] % MOD;
            ans = (ans + ways) % MOD;
            i += cnt;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
