N = 5*10**5 + 5

def factorial(n):
    fac = [1] * (n + 1)
    for i in range(1, n + 1):
        fac[i] = fac[i - 1] * i % mod
    return fac

def inv(n):
    return pow(n, mod - 2, mod)

def C(n, m):
    if n < m:
        return 0
    return fac[n] * inv(fac[m]) % mod * inv(fac[n - m]) % mod

