    int n;
    scanf("%d", &n);
    ll t[n], a[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &t[i], &a[i]);
    }
    ll ans = 1;
    for (int i = 0; i < n; i++) {
        ll g = gcd(t[i], a[i]);
        t[i] /= g;
        a[i] /= g;
        ans *= g;
    }
    ll sum = 0;
    for (int i = 0; i < n; i++) {
        sum += t[i] + a[i];
    }
    printf("%lld\n", sum * ans);
    return 0;
