        ll t = a[i].first, A = a[i].second;
        ll g = gcd(t, A);
        t /= g;
        A /= g;
        ans = (ans * t * A) / gcd(ans * t, A);
    }

    ll sum = 0;
    for (int i = 0; i < n; i++) {
        ll t = a[i].first, A = a[i].second;
        ll g = gcd(t, A);
        t /= g;
        A /= g;
        sum += t + A - 1;
    }

