bitset<M> g[6];
int vis[6][4];

void dfs(int x, int y) {
    if (x == 6) {
        int tmp = 0;
        for (int i = 0; i < 6; i++) {
            for (int j = 0; j < 4; j++) {
                if (vis[i][j]) {
                    tmp |= 1 << c[f[i][j]];
                }
            }
        }
        if (tmp == 0) {
            ans++;
        }
        return;
    }
    for (int i = 0; i < 4; i++) {
        if (!vis[x][i]) {
            vis[x][i] = 1;
            f[x][i] = y;
            dfs(x + 1, y);
            vis[x][i] = 0;
        }
    }
}

int main() {
    cin >> n;
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < 4; j++) {
            cin >> c[i][j];
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < 4; j++) {
            g[i][c[i][j]] = 1;
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < 4; j++) {
            for (int k = 0; k < 4; k++) {
                if (g[i][c[i][j]] & g[i][c[i][k]]) {
                    f[0][j] = i;
                    f[0][k] = i;
                    f[1][j] = i;
                    f[1][k] = i;
                    f[2][j] = i;
                    f[2][k] = i;
                    f[3][j] = i;
                    f[3][k] = i;
