#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MOD 1000000007
#define MAXN 55

int n, m, k;
int a[MAXN], b[MAXN];
int d[MAXN], e[MAXN];
int f[MAXN][MAXN];
int g[MAXN][MAXN];
int h[MAXN][MAXN];
int c[MAXN][MAXN];
int dp[MAXN][MAXN];

void dfs(int u, int p) {
    for (int i = 1; i <= n; i++) {
        if (i == p) continue;
        if (d[u] == i) {
            e[u] = i;
            break;
        }
    }
    for (int i = 1; i <= n; i++) {
        if (i == p) continue;
        if (d[i] == 0) {
            d[i] = d[u] + 1;
            dfs(i, u);
        }
    }
}

int main() {
    scanf("%d%d%d", &n, &m, &k);
    for (int i = 1; i <= m; i++) {
        scanf("%d%d", &a[i], &b[i]);
    }
    for (int i = 1; i <= n; i++) {
        d[i] = 0;
    }
    dfs(1, 0);
    for (int i = 1; i <= m; i++) {
        int u = a[i], v = b[i];
        if (u > v) {
            int temp = u;
            u = v;
            v = temp;
        }
        if (e[u] == e[v]) {
            f[u][v]++;
            f[v][u]++;
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            g[i][j] = (g[i][j - 1] + f[i][j]) % MOD;
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            h[i][j] = (h[i - 1][j] + g[i][j]) % MOD;
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            c[i][j] = (c[i - 1][j] + h[i][j]) % MOD;
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            dp[i][j] = (dp[i][j - 1] + c[i][j]) % MOD;
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                dp[i][j] = (dp[i][j] + 1LL * dp[i][k] * dp[k][j] % MOD * k % MOD) % MOD;
            }
        }
    }
