            }
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                for (int l = 1; l <= n; l++) {
                    ans = (ans + 1LL * dp[i][j] * dp[j][k] % MOD * dp[k][l] % MOD * dp[l][i] % MOD * k % MOD * (k - 1) % MOD * l % MOD * (l - 1) % MOD) % MOD;
                }
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                for (int l = 1; l <= n; l++) {
