#define MAX_W 100000
#define MAX_H 100000

int main() {
    int W, H;
    scanf("%d %d", &W, &H);

    long long p[MAX_W];
    long long q[MAX_H];

    for (int i = 0; i < W; i++) {
        scanf("%lld", &p[i]);
    }

    for (int j = 0; j < H; j++) {
        scanf("%lld", &q[j]);
    }

    long long dp[MAX_W][MAX_H];

    for (int i = 0; i < W; i++) {
        for (int j = 0; j < H; j++) {
            if (i == 0 && j == 0) {
                dp[i][j] = 0;
            } else if (i == 0) {
                dp[i][j] = dp[i][j - 1] + q[j - 1];
            } else if (j == 0) {
                dp[i][j] = dp[i - 1][j] + p[i - 1];
            } else {
                dp[i][j] = p[i - 1] + q[j - 1] + (dp[i - 1][j] < dp[i][j - 1] ? dp[i - 1][j] : dp[i][j - 1]);
            }
        }
    }

    printf("%lld\n", dp[W - 1][H - 1]);

    return 0;
}
