int dfs(char **grid, int h, int w, int i, int j) {
    if (i < 0 || i >= h || j < 0 || j >= w || grid[i][j] == 'X') {
        return 0;
    }
    grid[i][j] = 'X';
    return 1 + dfs(grid, h, w, i - 1, j) + dfs(grid, h, w, i + 1, j) +
           dfs(grid, h, w, i, j - 1) + dfs(grid, h, w, i, j + 1);
}

