from collections import deque

def min_fences(h, w, grid):
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    visited = [[False]*w for _ in range(h)]
    queue = deque()

    for i in range(h):
        for j in range(w):
            if grid[i][j] == 'X':
                for d in directions:
                    ni, nj = i + d[0], j + d[1]
                    if 0 <= ni < h and 0 <= nj < w and grid[ni][nj] == '.':
                        queue.append((ni, nj))
                        visited[ni][nj] = True

    fences = 0
    while queue:
        size = len(queue)
        for _ in range(size):
            x, y = queue.popleft()
            for d in directions:
                nx, ny = x + d[0], y + d[1]
                if 0 <= nx < h and 0 <= ny < w and not visited[nx][ny]:
                    if grid[nx][ny] == '.':
                        queue.append((nx, ny))
                        visited[nx][ny] = True
                    else:
                        fences += 1
                        visited[nx][ny] = True

    return fences

h, w = map(int, input().split())
grid = [list(input()) for _ in range(h)]
print(min_fences(h, w, grid))
