#include <stdio.h>
#include <string.h>

int min(int a, int b) {
    return a < b ? a : b;
}

int max(int a, int b) {
    return a > b ? a : b;
}

int eval(char *s, int *len) {
    int i, j, k, n, m, a, b, c;
    n = strlen(s);
    for (i = 0; i < n; i++) {
        if (s[i] == '?') {
            *len = i + 1;
            break;
        }
    }
    if (s[0] == '^') {
        for (i = 1; i < n; i++) {
            if (s[i] == ',') {
                break;
            }
        }
        a = eval(s + 1, len);
        b = eval(s + i + 1, len);
        return max(a, b);
    } else if (s[0] == '_') {
        for (i = 1; i < n; i++) {
            if (s[i] == ',') {
                break;
            }
        }
        a = eval(s + 1, len);
        b = eval(s + i + 1, len);
        return min(a, b);
    } else {
        for (i = 0; i < n; i++) {
            if (s[i] == ',') {
                break;
            }
        }
        a = s[0] - '0';
        b = eval(s + i + 1, len);
        return min(a, b);
    }
}

int main() {
    int q, i, len;
    char s[1001];
    scanf("%d", &q);
    while (q--) {
        scanf("%s", s);
        printf("%d %d\n", eval(s, &len), len);
    }
    return 0;
}
