```java
import java.io.*;
import java.util.*;

public class Main {
    static final int N = (int) 1e5 + 5;
    static final int M = (int) 1e9 + 7;
    static int n, k;
    static int[] a = new int[N];
    static int[] sz = new int[N];
    static int[] color = new int[N];
    static int[] vis = new int[N];
    static int[] id = new int[N];
    static int[] cnt = new int[N];
    static int[] dp = new int[N];
    static int[] fac = new int[N];
    static int[] inv = new int[N];
    static int[] invfac = new int[N];

    static int read(BufferedReader br) throws IOException {
        int x = 0, f = 1;
        int ch = br.read();
        while (ch < '0' || ch > '9') {
            if (ch == '-') f = -1;
            ch = br.read();
        }
        while (ch >= '0' && ch <= '9') {
            x = x * 10 + ch - '0';
            ch = br.read();
        }
        return x * f;
    }

    static void dfs(int u) {
        vis[u] = 1;
        sz[u] = 1;
        for (int v = 1; v <= n; v++) {
            if (a[u] == v && vis[v] == 0) {
                dfs(v);
                sz[u] += sz[v];
            }
        }
    }

    static void dfs2(int u) {
        vis[u] = 1;
        for (int v = 1; v <= n; v++) {
            if (a[u] == v && vis[v] == 0) {
                dfs2(v);
                color[id[v]] = color[id[u]];
            }
        }
    }

    static int C(int n, int m) {
        return (int) ((long) fac[n] * invfac[m] % M * invfac[n - m] % M);
    }

    static void solve(BufferedReader br) throws IOException {
        n = read(br);
        k = read(br);
        for (int i = 1; i <= n; i++) {
            a[i] = read(br);
            id[i] = i;
        }
        for (int i = 1; i <= n; i++) {
            if (vis[i] == 0) {
                dfs(i);
            }
        }
        for (int i = 1; i <= n; i++) {
            vis[i] = 0;
        }
        for (int i = 1; i <= n; i++) {
            if (vis[i] == 0) {
                dfs2(i);
                color[id[i]] = ++cnt[sz[i]];
            }
        }
        for (int i = 1; i <= n; i++) {
            if (color[i] == 1) {
                dp[1]++;
            } else {
                dp[cnt[sz[i]]]++;
            }
        }
        fac[0] = 1;
        for (int i = 1; i <= n; i++) {
            fac[i] = (int) ((long) fac[i - 1] * i % M);
        }
        inv[0] = inv[1] = 1;
        for (int i = 2; i <= n; i++) {
            inv[i] = (int) ((long) (M - M / i) * inv[M % i] % M);
        }
        invfac[0] = 1;
        for (int i = 1; i <= n; i++) {
            invfac[i] = (int) ((long) invfac[i - 1] * inv[i] % M);
        }
        long ans = 0;
        for (int i = 1; i <= n; i++) {
            dp[i] = (int) ((long) dp[i] * fac[k] % M * invfac[k - cnt[i]] % M);
            ans = (ans + dp[i]) % M;
        }
        System.out.println(ans);
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int T = 1;
        while (T-- > 0) {
            solve(br);
        }
    }
}
