```c
#include <stdio.h>
#include <stdlib.h>

#define MOD 924844033

long long factorial(int n) {
    long long result = 1;
    for (int i = 2; i <= n; i++) {
        result = (result * i) % MOD;
    }
    return result;
}

long long mod_inverse(long long a) {
    long long b = MOD, u = 1, v = 0;
    while (a) {
        long long t = b / a;
        b -= t * a;
        a = b;
        b = MOD % b;
        u -= t * v;
        v = u;
        u = t;
    }
    return v < 0 ? v + MOD : v;
}

long long ncr(int n, int r) {
    long long numerator = factorial(n);
    long long denominator = (factorial(r) * factorial(n - r)) % MOD;
    long long inv_denominator = mod_inverse(denominator);
    return (numerator * inv_denominator) % MOD;
}

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    long long result = factorial(n);
    for (int i = 0; i <= k; i++) {
        result -= ncr(n - k - 1, i) * ncr(k + 1, k - i + 1) % MOD;
    }
    for (int i = 0; i <= k; i++) {
        result -= ncr(n - k - 1, i) * ncr(k + 1, k - i) % MOD;
    }
    result = (result % MOD + MOD) % MOD;
    printf("%lld\n", result);
    return 0;
}
