const int N = 2005;
const int mod = 924844033;

int n, k, fac[N], inv[N];

int qpow(int a, int b) {
    int res = 1;
    while (b) {
        if (b & 1) res = (long long)res * a % mod;
        a = (long long)a * a % mod;
        b >>= 1;
    }
    return res;
}

int C(int n, int m) {
    if (n < m) return 0;
    return (long long)fac[n] * inv[m] % mod * inv[n - m] % mod;
}

int main() {
    scanf("%d%d", &n, &k);
    fac[0] = 1;
    for (int i = 1; i <= n; i++) fac[i] = (long long)fac[i - 1] * i % mod;
    inv[n] = qpow(fac[n], mod - 2);
    for (int i = n - 1; i >= 0; i--) inv[i] = (long long)inv[i + 1] * (i + 1) % mod;
    int ans = 0;
    for (int i = 0; i <= n - k - 1; i++) {
        int x = C(n - k - 1, i);
        int y = C(n - k - 1 + k, i);
        ans = (ans + (long long)x * (y - C(n - k - 1, i - 1)) % mod * fac[i] % mod * fac[n - k - 1 - i]) % mod;
    }
    printf("%d\n", (ans + mod) % mod);
    return 0;
