    if k == 0:
        print((factorial(n) - n) % mod)
        return
    ans = factorial(n)
    for i in range(1, n + 1):
        if i - k < 1 or i + k > n:
            continue
        ans = (ans * (i - k - 1)) % mod
        ans = (ans * (i + k - 1)) % mod
    ans = (ans * mod_inverse(factorial(n - 2 * k), mod)) % mod
    print((ans - n + 2 * k) % mod)

