#define MAXN 200005

typedef struct {
    int to;
    int next;
} Edge;

Edge edges[MAXN << 1];
int head[MAXN];
int tot;

void addEdge(int u, int v) {
    edges[tot].to = v;
    edges[tot].next = head[u];
    head[u] = tot++;
}

int n, x, y;
int red[MAXN], blue[MAXN];
int red_cnt, blue_cnt;
bool vis[MAXN];

void dfs_red(int u, int fa) {
    vis[u] = true;
    for (int i = head[u]; i != -1; i = edges[i].next) {
        int v = edges[i].to;
        if (v == fa) continue;
        if (blue[v] != -1) {
            red_cnt++;
            dfs_red(v, u);
        }
    }
}

void dfs_blue(int u, int fa) {
    vis[u] = true;
    for (int i = head[u]; i != -1; i = edges[i].next) {
        int v = edges[i].to;
        if (v == fa) continue;
        if (red[v] != -1) {
            blue_cnt++;
            dfs_blue(v, u);
        }
    }
}

