int d[2][MAXN];

void bfs(int c, int s) {
    queue<int> q;
    q.push(s);
    d[c][s] = 0;
    unordered_set<int> vis;
    vis.insert(s);
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : g[c][u]) {
            if (vis.find(v) == vis.end()) {
                vis.insert(v);
                d[c][v] = d[c][u] + 1;
                q.push(v);
            }
        }
    }
}

int main() {
