        cin >> a >> b;
        a--;
        b--;
        g[0][a].push_back(b);
        g[0][b].push_back(a);
        g[1][a].push_back(b);
        g[1][b].push_back(a);
    }
    bfs(0, x);
    bfs(1, y);
