    sz[u] = 1;
    for (int i = 0; i < n; i++) {
        if (a[i] == u) {
            dfs(b[i], u);
            sz[u] += sz[b[i]];
        }
    }
}

int lca(int u, int v) {
    while (fa[u] != fa[v]) {
        if (sz[fa[u]] > sz[fa[v]]) {
            u = fa[u];
        } else {
            v = fa[v];
        }
    }
    return fa[u];
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &a[i], &b[i]);
        a[i]--;
        b[i]--;
    }
    dfs(0, 0);
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (i != j) {
                ans[i] = (ans[i] + 1ll * sz[lca(i, j)] * (sz[i] + sz[j] - 2 * sz[lca(i, j)]) % MOD) % MOD;
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        printf("%lld\n", (ans[i - 1] + MOD) % MOD);
    }
    return 0;
